import Banner from '../../components/Banner'
import Card from '../../components/Card'
import BotaoDownload from '../../components/BotaoDownload'
import styles from './Inicio.module.css'

// === FUNCIONALIDADES DO STUDIO (GESTÃO) ===
const funcionalidades = [
  {
    titulo: "Agendamentos",
    preco: "Controle total de horários",
    imagem: "/imagens/agendamento.jpg",
  },
  {
    titulo: "Gestão de Clientes",
    preco: "Histórico e dados organizados",
    imagem: "/imagens/clientes.jpg",
  },
  {
    titulo: "Serviços e Preços",
    preco: "Tabela personalizável",
    imagem: "/imagens/servicos.jpg",
  },
  {
    titulo: "Relatórios",
    preco: "Análise de desempenho",
    imagem: "/imagens/relatorios.jpg",
  },
  {
    titulo: "Notificações",
    preco: "Lembretes automáticos",
    imagem: "/imagens/notificacoes.jpg",
  },
  {
    titulo: "Multiplataforma",
    preco: "App + Site + Web",
    imagem: "/imagens/multiplataforma.jpg",
  },
]

export default function Inicio() {
  return (
    <>
      <Banner />
      
      <section className={styles.sobre}>
        <h2>Sobre o Studio</h2>
        <p>
          <strong>Studio</strong> é uma ferramenta completa de gestão para profissionais da beleza. 
          Desenvolvido com React Native, Expo e Firebase, permite organizar agendamentos, clientes, serviços e horários com facilidade. 
          Ideal para barbeiros, cabeleireiros, manicures, esteticistas e massagistas.
        </p>
      </section>

      <section className={styles.destaques}>
        <h2>Funcionalidades do Sistema</h2>
        <div className={styles.cards}>
          {funcionalidades.map((f, i) => (
            <Card key={i} titulo={f.titulo} preco={f.preco} imagem={f.imagem} />
          ))}
        </div>
      </section>

      <BotaoDownload />
    </>
  )
}
